mapper.select:
SELECT DG.*
${processSelect}

    ,'${processDefinitionId}' AS SPD_ID,SPS.SPS_AUDIT_STATUS,
    (
    CASE
    WHEN SPS.SPS_AUDIT_STATUS = -1 AND SPS_BACK_STATUS = 1
    THEN '审核退回'
    WHEN SPS.SPS_AUDIT_STATUS = -1 AND SPS_BACK_STATUS = 2
    THEN '撤回审核'
    WHEN BPS.SDI_NAME IS NOT NULL
    THEN BPS.SDI_NAME ELSE '未启动'
    END
    ) AS PROCESS_STATUS_NAME

FROM(
    SELECT *
    FROM ${view}


            ${whereClause}



        order by ${orderByClause}



            limit ${offset}, ${limit}


            limit ${limit}


)DG

LEFT JOIN SYS_PROCESS_SCHEDULE SPS ON SPS.SPS_TABLE_ID = DG.ID AND SPS.SPS_IS_CANCEL = 0
LEFT JOIN SYS_DICT_INFO BPS ON BPS.SDT_CODE = 'SYS_PROCESS_STATUS' AND BPS.SDI_CODE = SPS.SPS_AUDIT_STATUS

${processJoin}

    ${processWhere}
