mapper.select:
SELECT SM.*,SMP.SM_NAME AS SM_PARENT_NAME,SC.SC_NAME,SPD.SPD_NAME
FROM(
    SELECT SM.*
    FROM SYS_MENU SM


            AND SM.SM_PARENTID = #{SM_PARENTID}


            AND SM.ID = #{ID}


            AND SM.SM_TYPE = #{SM_TYPE}


            AND SM.SM_CODE = #{SM_CODE}


            AND SM.SM_URL = #{SM_URL}


            AND SM.SC_ID = #{SC_ID}


            AND SM.ID != #{NOT_ID}


)SM
LEFT JOIN SYS_MENU SMP ON SMP.ID = SM.SM_PARENTID
LEFT JOIN SYS_CONFIGURE SC ON SC.ID = SM.SC_ID
LEFT JOIN SYS_PROCESS_DEFINITION SPD ON SPD.ID = SM.SPD_ID
ORDER BY SM.SM_ORDER ASC

SELECT COUNT(*)
FROM SYS_MENU


        AND SM_PARENTID = #{SM_PARENTID}


        AND ID = #{ID}


        AND SM_TYPE = #{SM_TYPE}


        AND SM_CODE = #{SM_CODE}


        AND SM_URL = #{SM_URL}


        AND ID != #{NOT_ID}

mapper.update:
UPDATE SYS_MENU
ID=#{ID}, SPD_ID=#{SPD_ID}, SM_NAME=#{SM_NAME}, SM_PARENTID=#{SM_PARENTID}, SM_CODE=#{SM_CODE}, SM_URL=#{SM_URL}, SM_CLASSICON=#{SM_CLASSICON}, SM_TYPE=#{SM_TYPE}, SM_ORDER=#{SM_ORDER}, IS_STATUS=#{IS_STATUS}, SC_ID=#{SC_ID}, SM_IS_LEAF=#{SM_IS_LEAF}, SM_IS_EXPAND=#{SM_IS_EXPAND}, SM_URL_PARAMS=#{SM_URL_PARAMS},
ID=#{ID}

mapper.select:
SELECT * FROM ( SELECT SB.* FROM SYS_OPERATOR_ROLE SOR INNER JOIN SYS_ROLE_MENU SRM ON SRM.SR_ID = SOR.SR_ID INNER JOIN SYS_ROLE_BUTTON SRB ON SRB.SRM_ID = SRM.ID INNER JOIN SYS_MENU SM ON SM.ID = SRM.SM_ID AND SM.IS_STATUS = 1 INNER JOIN SYS_MENU_BUTTON SMB ON SMB.SM_ID = SM.ID AND SMB.SB_ID = SRB.SB_ID INNER JOIN SYS_BUTTON SB ON SB.ID = SMB.SB_ID AND SM.IS_STATUS = 1 WHERE SOR.SO_ID = #{SO_ID} AND SM.ID = #{SM_ID} UNION ALL SELECT SB.* FROM SYS_MENU SM INNER JOIN SYS_MENU_BUTTON SMB ON SMB.SM_ID = SM.ID INNER JOIN SYS_BUTTON SB ON SB.ID = SMB.SB_ID AND SM.IS_STATUS = 1 WHERE SM.ID = #{SM_ID} AND SB.SB_CODE = 'RETURN' )TEMP
AND SB_TYPE = #{SB_TYPE}
GROUP BY ID ORDER BY SB_TYPE ASC,SB_ORDER ASC

SELECT SMB.*,SB.SB_NAME,SB.SB_CODE
FROM(
    SELECT SMB.*
    FROM SYS_MENU_BUTTON SMB


            AND SMB.ID = #{ID}


            AND SMB.SM_ID = #{SM_ID}


)SMB
INNER JOIN SYS_BUTTON SB ON SB.ID = SMB.SB_ID
ORDER BY SB.SB_TYPE ASC,SB.SB_ORDER ASC

mapper.update:
UPDATE SYS_MENU_BUTTON
ID=#{ID}, SM_ID=#{SM_ID}, SB_ID=#{SB_ID},
ID=#{ID}

mapper.delete:
DELETE FROM SYS_MENU_BUTTON


        AND ID=#{ID}


        AND SB_ID=#{SB_ID}


        AND SM_ID=#{SM_ID}
